import React, { useState } from "react";

export default function Form(props) {
  const handleUpClick = () => {
    // console.log("Uppercase was clicked" + text);
    let newText = text.toUpperCase();
    setText(newText);
  };
  const handleDownClick = () => {
    // console.log("Lowercase was clicked" + text);
    let newText = text.toLowerCase();
    setText(newText);
  };
  const handleClearclick = () => {
    let newText = "";
    setText(newText);
  };
  const handleCopyclick = () => {
    navigator.clipboard.writeText(text); 
    
  };
  const handleWhiteSpace = () => {
    let newText = text.split(/[ ]+/);
    setText(newText.join(" "));
  };
  const handleOnChange = (event) => {
    // console.log("On Change");
    setText(event.target.value);
  };

  const [text, setText] = useState("Enter Text Here");
  // setText("new text");
  return (
    <>
     <div className="flex flex-col items-center justify-center min-h-screen">
  <label className="mybox flex justify-center items-center text-center mb-5">
    {props.heading}
  </label>
  <div className="box-content w-full max-w-screen-md">
    <label
      htmlFor="message"
      className="block mb-2 text-sm font-medium text-gray-900 dark:text-white text-center"
    ></label>
    <textarea
      id="message"
      value={text}
      onChange={handleOnChange}
      rows="5"
      className="block p-2.5 text-lg text-gray-900 bg-gray-50 rounded-lg border border-gray-300 focus:ring-blue-500 focus:border-blue-500 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500 mt-3 w-full font-mono"
      placeholder="Write text here to analyze..."
    ></textarea>
  </div>

  <div className="flex justify-center space-x-5 mt-5">
    <button
      className="bg-blue-500 hover:bg-blue-400 text-white font-bold py-2 px-4 border-b-4 border-blue-700 hover:border-blue-500 rounded"
      onClick={handleUpClick}
    >
      Uppercase
    </button>
    <button
      className="bg-blue-500 hover:bg-blue-400 text-white font-bold py-2 px-4 border-b-4 border-blue-700 hover:border-blue-500 rounded"
      onClick={handleDownClick}
    >
      Lowercase
    </button>
    <button
      className="bg-blue-500 hover:bg-blue-400 text-white font-bold py-2 px-4 border-b-4 border-blue-700 hover:border-blue-500 rounded"
      onClick={handleCopyclick}
    >
      Copy Text
    </button>
    <button
      className="bg-blue-500 hover:bg-blue-400 text-white font-bold py-2 px-4 border-b-4 border-blue-700 hover:border-blue-500 rounded"
      onClick={handleWhiteSpace}
    >
      Whitespace
    </button>
    <button
      className="bg-blue-500 hover:bg-blue-400 text-white font-bold py-2 px-4 border-b-4 border-blue-700 hover:border-blue-500 rounded"
      onClick={handleClearclick}
    >
      Clear Text
    </button>
  </div>
</div>

<h2 className="text-center text-white font-mono text-xl mt-10">Stats for Nerds ðŸ¤“</h2>
<div className="text-white font-mono text-xl p-2.5 max-w-screen-md">
  <h4 className="text-center">
    Text Summary:{" "}
    <u className="text-teal-400 font-mono">
      <b>
        {text.split(" ").filter((element) => {
          return element.length !== 0;
        }).length}
      </b>
    </u>{" "}
    words and{" "}
    <u className="text-teal-400 font-mono">
      <b>{text.length}</b>
    </u>{" "}
    characters
  </h4>
  <h4 className="text-center">
    Time Required to read text:{" "}
    <u className="text-teal-400 font-mono">
      <b>{(0.008 * text.split(" ").length).toFixed(2)}</b>
    </u>{" "}
    minutes or{" "}
    <u className="text-teal-400 font-mono">
      <b>{(0.48 * text.split(" ").length).toFixed(2)}</b>
    </u>{" "}
    seconds
  </h4>
</div>

<h2 className="text-center text-white font-mono text-xl mt-10">Preview ðŸ‘€</h2>
<div className="text-white font-mono text-xl p-2.5 max-w-screen-md">
  <h4 className="text-center">
    {text.length > 0
      ? text
      : "Enter text in the above textbox to preview here"}
  </h4>
</div>

    </>
  );
}
